import React from 'react';
import './HomePage.css';
import Banner from '../../components/Banner/Banner';
import NewStackButton from '../../components/NewStackButton/NewStackButton';
import StackCard from '../../components/StackCard/StackCard';
import CreateStackModal from '../../components/CreateStackModal/CreateStackModal';

interface Stack {
    title: string;
    description: string;
    links: string[];

}

const HomePage: React.FC = () => {
    const [isModalOpen, setIsModalOpen] = React.useState(false);
    const [stacks, setStacks] = React.useState<Stack[]>([]);

    const handleAddStack = () => {
        setIsModalOpen(true);
    };

    const handleCreateStack = (stack:Stack) => {
        setStacks([...stacks, stack]);
    };

    return (
        <div className="home-container">
            <Banner/>
            <NewStackButton/>
            <div className="stacks-container">
                My Stacks
                <div className="stack-card-list">
                    <StackCard/>
                </div>
            </div>
            <CreateStackModal
            isOpen={isModalOpen}
            onRequestClose={() => setIsModalOpen(false)}
            onCreateStack={handleCreateStack}
        />
        </div>
    );
};

export default HomePage;
