import React, {useState, useEffect} from 'react';
import axios from 'axios';
import './HomePage.css';
import Banner from '../../components/Banner/Banner';
import NewStackButton from '../../components/NewStackButton/NewStackButton';
import StackCard from '../../components/StackCard/StackCard';
import CreateStackModal from '../../components/CreateStackModal/CreateStackModal';

interface Item {
    id: number;
    type: string;
    content: string;
}

interface Stack {
    id: number;
    title: string;
    description: string;
    items: Item[];
}

const HomePage: React.FC = () => {
    const [showModal, setShowModal] = useState(false);
    const [stacks, setStacks] = useState<Stack[]>([]);

    const handleOpen = () => {
        setShowModal(true);
    }

    const handleClose = () => {
        setShowModal(false);
    }

    const addStack = (stack: Stack) => {
        setStacks([...stacks, stack])
    }

    const fetchStacks = async() => {
        try {
            const response = await axios.get('http://localhost:5000/api/stacks');
            setStacks(response.data);
        } catch (error) {
            console.error('Error fetching stacks:', error);
        }
    };

    useEffect(() => {
        fetchStacks();
    }, []);

    return (
        <div className="home-container">
            <Banner/>
            <button onClick={handleOpen} className="new-stack-button">
                Create new stack
            </button>
            <CreateStackModal show={showModal} handleClose={handleClose} addStack={addStack}/>
            <div className="stacks-container">
                My Stacks
                <div className="stack-card-list">
                    <StackCard/>
                </div>
            </div>
        </div>
    );
};

export default HomePage;
