import React, { FC, useState } from 'react';
import Modal from 'react-modal';
import './CreateStackModal.css';

interface ModalProps {
    show: boolean;
    handleClose: () => void;
    addItem: (item: string) => void
    items: string[];
}

const CreateStackModal: FC<ModalProps> = ({ show, handleClose, addItem, items }) => {

    const [inputValue, setInputValue] = useState('')

    if (!show) {
        return null;
    }

    const handleAddItem = () => {
        if (inputValue.trim()) {
            addItem(inputValue);
            setInputValue('');
        }
    }

    return (
        <div className="modal">
            <div className="modal-content">
                <span className="close" onClick={handleClose}>
                    &times;
                </span>
                <h2>Add a Link to the Stack</h2>
                <input
                    type="text"
                    value={inputValue}
                    onChange={(e) => setInputValue(e.target.value)}
                    placeholder="Enter Link"
                />
                <button onClick = {handleAddItem}>Add to Stack</button>
                <ul>
                    {items.map((item, index) =>(
                        <li key={index}>{item}</li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default CreateStackModal;
