import React, { useState } from 'react';
import Modal from 'react-modal';
import './CreateStackModal.css';

// Modal.setAppElement('#root');

interface CreateStackModalProps {
    isOpen: boolean;
    onRequestClose: () => void;
    onCreateStack: (stack: {title: string; description: string; links:string[]}) => void;
}

const CreateStackModal: React.FC<CreateStackModalProps> = ({isOpen, onRequestClose, onCreateStack}) => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [links, setLinks] = useState<string[]>(['']);

    const handleLinkChange = (index: number, value: string) => {
        const newLinks = [...links];
        newLinks[index] = value;
        setLinks(newLinks); 
    };

    const handleAddLink = () => {
        setLinks([...links, '']);
    };

    const handleSubmit = () => {
        onCreateStack({title, description, links});
        setTitle('');
        setDescription('');
        setLinks(['']);
        onRequestClose();
    };

    return (
        <Modal isOpen={isOpen} onRequestClose={onRequestClose} contentLabel="Create New Stack:">
            <h2>Create New Stack</h2>
            <form onSubmit={(e) => {e.preventDefault(); handleSubmit(); }}>
                <div>
                    <label>Title</label>
                    <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required/>
                </div>
                <div>
                    <label>Description</label>
                    <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} required/>
                </div>
                <div>
                    <label>Links</label>
                    {links.map((link, index) => (
                        <div key={index}>
                            <input
                                type="url"
                                value={link}
                                onChange={(e) => handleLinkChange(index, e.target.value)}
                                required
                            />
                        </div>
                    ))}
                    <button type="button" onClick={handleAddLink}>Add Another Link</button>
                </div>
                <button type="submit">Create Stack</button>
            </form>
            <button onClick={onRequestClose}>Close</button>
        </Modal>
    );
};

export default CreateStackModal;
